package JavaBasics;

public class Arithmtic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	  
		
		int i = 100;
		int j = 200;
		
	//	Addition (+)
        int k = i+j;
        System.out.println("the addition i and j is="+k);
        
     //Subtraction   (-)
        int l = i-j;
        System.out.println("the subtraction of i-j is ="+l);
        
       //Multiply (/)
        int m = i/j;
        System.out.println("Multiply i and j is ="+m);
        
       // Percentage (%)
        int n = i%j;
        System.out.println("the percentage of i and j is="+n);
	}

}
