package JavaBasics;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("My name is Imran");
		
		// (1) boolean data type
		Boolean b = true;
		System.out.println("Our boolean value is =" +b);
		
	   //	(2) Character Data type
		
		char c = '$';
		System.out.println("Our pays come in ="+c);
		
		
		//  (3) Byte Data Type
		
		byte d = 100;
		System.out.println("Our hourly pay is around ="+d);
		
		// (4)Short value Data Type
		short e = 1234;
		System.out.println("Our short value is ="+e);
		
		// (5) long value Data type
		long f = 897878077L;
		System.out.println("this is our long value="+f);
		
		// (6) Floating value Data Type
		float g = 23.98F;
		System.out.println("The floating value is="+g);
		
		// (7) Integers in Data Type 
		int h = 1;
		System.out.println("THe integer value is ="+h);
		
		// (8) Double in Data Type
		double i = 100;
		System.out.println("Double value is ="+i);
		
		
		
		  
	}
}
